<?php get_header(); ?>	<div id="main_cont">	<div class="container">		<div id="cont_left">			<?php 			if (is_category()) { ?>				<div class="archive_title">							<?php printf( __( '%s', 'twentyten' ), '<span>' . single_cat_title( '', false ) . '</span>' ); ?>						</div><!--//archive_title-->			<?php 			}			global $wp_query;			$args = array_merge( $wp_query->query, array( 				'posts_per_page' => -1, 				'rewrite' => array(					'slug' => 'filme', 					'with_front' => false) 				) 			);			query_posts( $args );        			$x = 0;			while (have_posts()) : the_post(); ?>     				<div class="post_box">					<div class="left">						<?php if(get_post_meta( get_the_ID(), 'page_featured_type', true ) == 'youtube') { ?>							<iframe width="560" height="315" src="http://www.youtube.com/embed/<?php echo get_post_meta( get_the_ID(), 'page_video_id', true ); ?>" frameborder="0" allowfullscreen></iframe>						<?php } elseif(get_post_meta( get_the_ID(), 'page_featured_type', true ) == 'vimeo') { ?>							<iframe src="http://player.vimeo.com/video/<?php echo get_post_meta( get_the_ID(), 'page_video_id', true ); ?>?title=0&amp;byline=0&amp;portrait=0&amp;color=085e17" width="500" height="338" frameborder="0" webkitAllowFullScreen mozallowfullscreen allowFullScreen></iframe>						<?php } else { ?>							<a href="<?php the_permalink(); ?>" style="display: block !important; text-align: center !important;">								<?php 									$src = get_the_post_thumbnail_url('post-image');									if (empty($src)) {										$src = "/wp-content/uploads/2018/02/movie-icon-14032.png";									}								?>								<div class="container-search">									<div class="tag-search">										<?php 											$tipo = ucfirst(get_post_type()); 											if ($tipo == 'Page') {												$tipo = 'PÃ¡gina';											} elseif ($tipo == 'Post') {												$tipo = 'Blog';											}																						echo $tipo;										?>									</div>									<img src="<?php echo $src; ?>" 										alt="<?php echo get_the_title(); ?>" 										title="<?php echo get_the_title(); ?>"										class="attachment-post-image default-featured-img"										style="float: none !important; display: inline !important; width: auto !important;"										height="240">								</div>							</a>						<?php } ?>																</div>					<div class="right">						<h3><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h3>						<p><?php echo custom_field_excerpt(); ?></p>						<div class="post_meta">							<div class="post_meta_left"><?php the_time('F d, Y'); ?></div>							<div class="post_meta_right"><a href="<?php the_permalink(); ?>">LEIA MAIS</a></div>							<div class="clear"></div>						</div><!--//post_meta-->					</div>					<div class="clear"></div>				</div><!--//post_box-->			<?php $x++; ?>			<?php endwhile; ?>					</div><!--//cont_left-->			<?php get_sidebar(); ?>				<div class="clear"></div>			</div><!--//container-->	</div><!--//main_cont--><?php get_footer(); ?> 