<?php get_header(); ?>	<div id="main_cont">	<div class="container">		<div id="cont_left">			<?php			global $wp_query;			global $_GET;			// COMEÇO DA PESQUISA POR NOMES DE TAXONOMIAS			$terms = get_terms( array(			    'taxonomy'      => array('pessoa', 'empresa'), // taxonomy name			    'orderby'       => 'id', 			    'order'         => 'ASC',			    'hide_empty'    => true,			    'fields'        => 'all',			    'name__like'    => $_GET['s']			));			      			$x = 0;			foreach ($terms as $term) { ?>				<!--				// No caso de existirem mais de 6 pessoas/empresas, só vai exibir 6 em tamanho grande.				// O resto vai exibir numa lista simples mesmo.				if ($x < 6)	{-->				<div class="post_box">										<?php // Pega imagem da taxonomia												$src = "";						$description = "";												// Verifica se tem algo na descrição da taxonomia. Se tiver, verifica se tem imagens.						if (!empty($term->description)) {							$result = array();							preg_match_all('/<img[^>]+>/i',$term->description, $result);														// Pegando valor do SRC da imagem							if (!(empty($result))) {								$tag_src = preg_match( '@src="([^"]+)"@' , $result[0][0], $match );								$src = array_pop($match);							} else {								$src = "/wp-content/themes/DesignerMagRes/images/anon.png";							}														// Removendo imagens da descrição (para não embeddar isso lá).							$description = preg_replace("/<img[^>]+\>/i", "", $term->description);						} else {							$src = "/wp-content/themes/DesignerMagRes/images/anon.png";							$description = "Ainda não há uma descrição para este registro.";						}																	?>										<?php					// No caso de existirem mais de 6 pessoas/empresas, só vai exibir 6 em tamanho grande.					// O resto vai exibir numa lista simples mesmo.					if ($x < 6)	{						?>						<div class="left">							<a href="<?php echo get_term_link($term); ?>" style="display: block !important; text-align: center !important;">								<div class="container-search">								   <div class="tag-search"><?php echo ucfirst($term->taxonomy); ?></div>								   <img src="<?php echo $src; ?>" 										alt="<?php echo $term->name; ?>" 										title="<?php echo $term->name; ?>"										class="attachment-post-image default-featured-img"										style="float: none !important; display: inline !important; width: auto !important;"										height="240">								</div>							</a>						</div>							<div class="right">								<h3>								<a href="<?php echo get_term_link($term); ?>"><?php echo $term->name; ?></a>							</h3>														<p> <?php echo custom_taxonomy_excerpt($term); ?> </p>														<div class="post_meta">								<div class="post_meta_right"><a href="<?php echo get_term_link($term); ?>">LEIA MAIS</a></div>								<div class="clear"></div>										</div><!--//post_meta-->							</div>										<?php } else {						if ($x == 6) echo '<ul>';												if ($x >= 6) { ?>							<li>								<h3><a href="<?php echo get_term_link($term); ?>"><?php echo $term->name; ?></a></h3>							</li>							<?php } ?>											<?php } ?>					<div class="clear"></div>				</div><!--//post_box-->				<?php $x++; ?>			<?php }			if ($x >= 6) echo '</ul>';						// COMEÇA A BUSCAR POR POSTS E CUSTOM POST TYPES			$args = array_merge( $wp_query->query, array( 'posts_per_page' => -1 ) );			query_posts( $args );						$x = 0;			while (have_posts()) : the_post(); ?>        				<div class="post_box">					<div class="left">						<?php if(get_post_meta( get_the_ID(), 'page_featured_type', true ) == 'youtube') { ?>							<iframe width="560" height="315" src="http://www.youtube.com/embed/<?php echo get_post_meta( get_the_ID(), 'page_video_id', true ); ?>" frameborder="0" allowfullscreen></iframe>						<?php } elseif(get_post_meta( get_the_ID(), 'page_featured_type', true ) == 'vimeo') { ?>							<iframe src="http://player.vimeo.com/video/<?php echo get_post_meta( get_the_ID(), 'page_video_id', true ); ?>?title=0&amp;byline=0&amp;portrait=0&amp;color=085e17" width="500" height="338" frameborder="0" webkitAllowFullScreen mozallowfullscreen allowFullScreen></iframe>						<?php } else { ?>													<a href="<?php the_permalink(); ?>" style="display: block !important; text-align: center !important;">								<?php 									$src = get_the_post_thumbnail_url('post-image');									if (empty($src)) {										$src = "/wp-content/uploads/2018/02/movie-icon-14032.png";									}								?>								<div class="container-search">									<div class="tag-search">										<?php 											$tipo = ucfirst(get_post_type(get_the_ID())); 											if ($tipo == 'Page') {												$tipo = 'Página';											} elseif ($tipo == 'Post') {												$tipo = 'Blog';											}																						echo $tipo;										?>									</div>									<img src="<?php echo $src; ?>" 										alt="<?php echo get_the_title(); ?>" 										title="<?php echo get_the_title(); ?>"										class="attachment-post-image default-featured-img"										style="float: none !important; display: inline !important; width: auto !important;"										height="240">								</div>							</a>						<?php } ?>																</div>					<div class="right">						<h3><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h3>						<!-- <p><?php // echo ds_get_excerpt('140'); ?></p> -->						<p><?php echo custom_field_excerpt(); ?></p>						<div class="post_meta">							<div class="post_meta_left"><?php the_time('F d, Y'); ?></div>							<div class="post_meta_right"><a href="<?php the_permalink(); ?>">LEIA MAIS</a></div>							<div class="clear"></div>						</div><!--//post_meta-->					</div>					<div class="clear"></div>				</div><!--//post_box-->			<?php $x++; ?>			<?php endwhile; ?>						<!-- FIM DOS POSTS -->														</div><!--//cont_left-->			<?php get_sidebar(); ?>				<div class="clear"></div>			</div><!--//container-->	</div><!--//main_cont--><?php get_footer(); ?> 